import React, {useState} from 'react';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';


function Item({layoutId, setHovered, setExpanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  return(
    <motion.li
      id={id}
      layoutId={layoutId}
      onHoverStart={() => setHovered(index)}
      onHoverEnd={() => setHovered(null)}
      onTouchEnd={() => setExpanded(index)}
      onClick={() => setExpanded(index)}
      initial={{opacity: 0, y: -100}}
      animate={{opacity: 1, y: 0}}
      exit={{opacity: 0, transition:{duration: 0.8}}}
      transition={{ type: "spring", duration: 3, bounce: 0.2}}
      className={"blue-bg"}
      // whileHover={{backgroundColor: 'rgba(80,80,250,0.7)', transition: { duration: 0.2 }}}
      >
        <motion.h2 layoutId={`left-${layoutId}`} className="h2-left">> {title}</motion.h2>
        <motion.h2 layoutId={`right-${layoutId}`} className="h2-right">{subtitle}</motion.h2>
    </motion.li>
  )
}

export default Item;
