import React, {useState, Fragment} from 'react';

import { motion, AnimatePresence } from 'framer-motion';

function Content(props) {
  // const [open, setOpen] = useState(false);
  const [hovered, setHovered] = useState(null)

  return(
      <div id="content-box">

        <div id="title-box">
          <h1>BOYAN LEVCHEV</h1>
          <h2>FULL STACK WEB DEVELOPER</h2>
          <h2>LINKEDIN:<a href="https://linkedin.com/in/boyanlevchev">linkedin.com/in/boyanlevchev</a></h2>
          <h2>GITHUB:<a href="https://github.com/boyanlevchev">github.com/boyanlevchev</a></h2>
        </div>

        <div id="links-box">

          <ul id="links">
            <motion.li id="muse-li" whileHover={() => setHovered(1)} onTouchStart={() => setHovered(1)}><a href="https://www.musei.io" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Musei</h2></a><h2 className="h2-right">A DIY virtual reality gallery builder for artists, galleries and curators</h2></motion.li>

            <li id="everything-li"><a href="https://everythingforever.net/nodes" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Everything Forever Nodes</h2></a><h2 className="h2-right">An exploration interface for the Royal College of Art exhibit Everything Forever</h2></li>

            <li id="froala-li"  onMouseEnter={() => setHovered(2)} onMouseLeave={() => setHovered(null)} onTouchStart={() => setHovered(2)}><a href="https://froala.com/wysiwyg-editor/whiteboard" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Froala Whiteboard</h2></a><h2 className="h2-right">A collaborative brainstorming tool</h2></li>

            <li id="froala-2-li"><a href="https://froala.com/wysiwyg-editor/pricing" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Froala Pricing Wizard</h2></a><h2 className="h2-right">A guided questionnaire to help you pick the right plan</h2></li>

            <li id="froala-3-li"><a href="https://froala.com/wysiwyg-editor/customizer-v3/" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Froala Editor Customizer</h2></a><h2 className="h2-right">A visual tool for granular customization of the Froala WYSIWYG Editor</h2></li>

            <li id="violeta-li" onMouseEnter={() => setHovered(3)} onMouseLeave={() => setHovered(null)} onTouchStart={() => setHovered(3)}><a href="http://violeta-popov-demo.herokuapp.com" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Violeta Popov M.D.</h2></a><h2 className="h2-right">A portfolio website for an NY-based medical practitioner - <br/> work in progress...</h2></li>

            <li id="wedding-li"><a href="https://jujubobo.wedding" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Wedding Save the Date</h2></a><h2 className="h2-right">A save-the-date website created for a couple getting married</h2></li>

          </ul>
        </div>

        <motion.div id="media-content">
          <AnimatePresence exitBeforeEnter>
            {hovered === 1 &&
              <motion.div
                layout
                key={1}
                initial={{opacity:0, scale: 0.95, x:-100}}
                animate={{opacity:1, scale:1, x:0}}
                exit={{opacity:0, scale: 0.95, x:100}}
                transition={{ type: "spring", duration: 1, bounce: 0.3}}
                className="muse-1 muse-images"></motion.div>
            }
{/*          </AnimatePresence>

          <AnimatePresence>*/}
{/*            {hovered === 1 &&
