import React, {useState} from 'react';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';

import ExpandedItem from '../components/expanded_item';
import Item from '../components/item';


function AnimatedItemGroup({setHovered, setExpanded, expanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  return(
    <AnimateSharedLayout>
      <Item
        layoutId={`portfolio-item-${index}`}
        id={id}
        index={index}
        href={"https://www.musei.io"}
        title={"Musei"}
        subtitle={"A DIY virtual reality gallery builder for artists, galleries and curators"}
        setExpanded={setExpanded}
        setHovered={setHovered}/>

      {expanded === index &&
        <ExpandedItem
          layoutId={`portfolio-item-${index}`}
          id={id}
          index={index}
          href={"https://www.musei.io"}
          title={"Musei"}
          subtitle={"A DIY virtual reality gallery builder for artists, galleries and curators"}
          setExpanded={setExpanded}/>
      }
    </AnimateSharedLayout>
  )
}

export default AnimatedItemGroup;
