import React, {useState, Fragment} from 'react';

import { motion, AnimatePresence } from 'framer-motion';

function Content(props) {
  // const [open, setOpen] = useState(false);
  const [hovered, setHovered] = useState(null)

  return(
      <div id="content-box">

        <div id="title-box">
          <h1>BOYAN LEVCHEV</h1>
          <h2>FULL STACK WEB DEVELOPER</h2>
          <h2>LINKEDIN:<a href="https://linkedin.com/in/boyanlevchev">linkedin.com/in/boyanlevchev</a></h2>
          <h2>GITHUB:<a href="https://github.com/boyanlevchev">github.com/boyanlevchev</a></h2>
        </div>

        <div id="links-box">

          <ul id="links">
            <li id="muse-li" onMouseEnter={() => setHovered(1)} onMouseLeave={() => setHovered(null)} onTouchStart={() => setHovered(1)}><a href="https://www.musei.io" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Musei</h2></a><h2 className="h2-right">A DIY virtual reality gallery builder for artists, galleries and curators</h2></li>

            <li id="froala-li"><a href="https://froala.com/wysiwyg-editor/whiteboard" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Froala Whiteboard</h2></a><h2 className="h2-right">A collaborative brainstorming tool</h2></li>

            <li id="froala-li"><a href="https://froala.com/wysiwyg-editor/pricing" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Froala Pricing Wizard</h2></a><h2 className="h2-right">A guided questionnaire to help you pick the right plan</h2></li>

            <li id="froala-li"><a href="https://froala.com/wysiwyg-editor/pricing" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Froala Pricing Wizard</h2></a><h2 className="h2-right">A guided questionnaire to help you pick the right plan</h2></li>

            <li id="violeta-li"><a href="http://violeta-popov-demo.herokuapp.com" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Violeta Popov M.D.</h2></a><h2 className="h2-right">A portfolio website for an NY-based medical practitioner - <br/> work in progress...</h2></li>

            <li id="violeta-li"><a href="https://jujubobo.wedding" target="_blank" rel="noopener noreferrer"><h2 className="h2-left">> Wedding Save the Date</h2></a><h2 className="h2-right">A save-the-date website created for a couple getting married</h2></li>

            <AnimatePresence>
              {hovered === 1 &&
                <Fragment>
                  <motion.div
                    initial={{opacity:0, scale: 0.95, x:100}}
                    animate={{opacity:1, scale:1, x:0}}
                    exit={{opacity:0, scale: 0.95, x:100}}
                    transition={{ type: "spring", duration: 2.5, bounce: 0.3}}
                    className="muse-1 muse-images"></motion.div>
                  <motion.div
                    initial={{opacity:0, scale: 0.95, x:-100}}
                    animate={{opacity:1, scale:1, x:0}}
                    exit={{opacity:0, scale: 0.95, x:-100}}
                    transition={{ type: "spring", duration: 2.5, bounce: 0.3}}
                    className="muse-2 muse-images"></motion.div>
                </Fragment>
              }
            </AnimatePresence>



            {hovered === 2 &&
              <Fragment>
                <div className="violeta-image violeta-1"></div>
                <div className="violeta-image violeta-2"></div>
                <div className="violeta-image violeta-3"></div>
              </Fragment>
            }

            {hovered === 3 &&
              <Fragment>
                <div className="froala-image froala-1"></div>
                <div className="froala-image froala-2"></div>
              </Fragment>
            }

          </ul>
        </div>

      </div>
    )
}

export default Content;
