import React, {useState} from 'react';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';


function Item({layoutId, setHovered, setExpanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  return(
    <motion.li
      id={id}
      layoutId={layoutId}
      initial={{opacity: 0, y: -100}}
      animate={{opacity: 1, y: 0}}
      exit={{opacity: 0}}
      transition={{type: 'spring', bounce: 0.3, duration: 3}}

      onHoverStart={() => setHovered(index)}
      onHoverEnd={() => setHovered(null)}
      onTouchEnd={() => setExpanded(index)}
      onClick={() => setExpanded(index)}
      className={"blue-bg"}
      >
        <motion.h2 layout className="h2-left">> {title}</motion.h2>
        <motion.h2 layout  className="h2-right">{subtitle}</motion.h2>
    </motion.li>
  )
}

export default Item;
