import React, {useState} from 'react';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';


function Item({layoutId, setHovered, setExpanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  return(
    <motion.li
      id={id}
      layoutId={layoutId}
      onHoverStart={() => setHovered(index)}
      onHoverEnd={() => setHovered(null)}
      onTouchEnd={() => setExpanded(index)}
      onClick={() => setExpanded(index)}
      initial={{backgroundColor: 'rgba(255,255,255,0)', opacity: 0, y: -50}}
      animate={{backgroundColor: 'rgba(255,255,255,0.8)', opacity: 1, y: 0}}
      >
        <h2 className="h2-left">> {title}</h2>
        <h2 className="h2-right">{subtitle}</h2>
    </motion.li>
  )
}

export default Item;
