import React, {useState} from 'react';
import { motion } from 'framer-motion';

import { projectDetails } from '../projects';

import AnimatedItemGroup from './animated_item_group';



function ItemList({setExpanded, setHovered, expanded, hovered}) {
  const [keys] = useState(Object.keys(projectDetails));

  return(
      <div
        id="links-box"
        onMouseOver={() => setHovered(null)}
        className={expanded !== null && "overflow-hidden"}
        >
        <motion.ul id="links">
          {keys.map((key, i) => {
            return <AnimatedItemGroup
                      id={key}
                      index={i+1}
                      href={projectDetails[key].href}
                      title={projectDetails[key].title}
                      subtitle={projectDetails[key].subtitle}
                      setExpanded={setExpanded}
                      hovered={hovered}
                      setHovered={setHovered}
                      expanded={expanded}/>
          })}

          <AnimatedItemGroup
            id={"everything"}
            index={2}
            href={"https://everythingforever.net/nodes"}
            title={"Everything Forever Nodes"}
            subtitle={"An exploration interface for the Royal College of Art exhibit Everything Forever"}
            setExpanded={setExpanded}
            hovered={hovered}
            setHovered={setHovered}
            expanded={expanded}/>

          <AnimatedItemGroup
            id={"froala"}
            index={3}
            href={"https://froala.com/wysiwyg-editor/whiteboard"}
            title={"Froala Whiteboard"}
            subtitle={"A collaborative brainstorming tool"}
            setExpanded={setExpanded}
            hovered={hovered}
            setHovered={setHovered}
            expanded={expanded}/>

          <AnimatedItemGroup
            id={"froala-2"}
            index={4}
            href={"https://froala.com/wysiwyg-editor/pricing"}
            title={"Froala Pricing Wizard"}
            subtitle={"A guided questionnaire to help you pick the right plan"}
            setExpanded={setExpanded}
            hovered={hovered}
            setHovered={setHovered}
            expanded={expanded}/>

          <AnimatedItemGroup
            id={"froala-3"}
            index={5}
            href={"https://froala.com/wysiwyg-editor/customizer-v3/"}
            title={"Froala Editor Customizer"}
            subtitle={"A visual tool for granular customization of the Froala WYSIWYG Editor"}
            setExpanded={setExpanded}
            hovered={hovered}
            setHovered={setHovered}
            expanded={expanded}/>

          <AnimatedItemGroup
            id={"violeta"}
            index={6}
            href={"http://violeta-popov-demo.herokuapp.com"}
            title={"Violeta Popov M.D."}
            subtitle={"A portfolio website for an NY-based medical practitioner - \nwork in progress..."}
            setExpanded={setExpanded}
            hovered={hovered}
            setHovered={setHovered}
            expanded={expanded}/>

          <AnimatedItemGroup
            id={"wedding"}
            index={7}
            href={"https://savethedate.jujubobo.wedding"}
            title={"Wedding Save the Date"}
            subtitle={"A save-the-date website created for a couple getting married"}
            setExpanded={setExpanded}
            hovered={hovered}
            setHovered={setHovered}
            expanded={expanded}/>

        </motion.ul>
      </div>
    )
}

export default ItemList;
