import React from 'react';
import { motion } from 'framer-motion';


const projectDetails = {
  "muse-li": {
    started: '03-2020',
    ended: 'ongoing',
    role: 'Full stack',
    technologies_used: {
      frontend: ['HTML5', 'CSS3', 'SASS/SCSS', 'Javascript ES6', 'A-Frame', 'three.js', 'jQuery', 'Bootstrap',],
      backend: ['Ruby', 'Rails', 'PostGreSQL', 'AWS S3', 'AWS Cloudfront', 'Heroku']
    },
    images: [
      {
      type: 'image',
      url: 'https://res.cloudinary.com/dt47helyp/image/upload/v1617044987/Screen_Shot_2021-03-29_at_8.08.06_PM_vg1asv.png',
      alt: 'Musei.io home page - make a new VR gallery, see artists, see spaces'
      },
      {
        type: 'image',
        url: 'https://res.cloudinary.com/dt47helyp/image/upload/v1617044991/Screen_Shot_2021-03-29_at_8.09.12_PM_p1dz1m.png',
        alt: 'Musei.io VR scene - Michelangelo in Bagan, Khayiminga temple'
      }
    ]
  },
  "everything-li": {
    started: '03-2021',
    ended: '04-2021',
    role: 'Front end',
    technologies_used: {
      frontend: ['React', 'Framer Motion', 'CSS3', 'SASS/SCSS', 'Javascript ES6'],
      backend: ['Google Firestore', 'AWS Lambda', 'AWS API Gateway', 'Squarespace']
    }
  },
  'froala-li': {
    started: '03-2020',
    ended: '07-2020',
    role: 'Front end',
    technologies_used: {
      frontend: ['React', 'Redux', 'CSS3', 'SASS/SCSS', 'Javascript ES6'],
      backend: ['Google Firebase Realtime NoSQL DB', 'AWS Lambda', 'AWS API Gateway', 'AWS S3', 'Wordpress']
    }
  }
}


function ExpandedItem({layoutId, setExpanded, expanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  return(

    <motion.div
      className={`expanded-item ${!expanded && 'pointer-events-none'}`}
      initial={{opacity: 0}}
      animate={{opacity: 1}}
      exit={{opacity: 0}}
      onTouchEnd={() => setExpanded(null)}
      onClick={() => setExpanded(null)}
      >
      <div
        className={"item-width"}
        onTouchEnd={ e => e.stopPropagation()}
        onClick={ e => e.stopPropagation()}
        >
        <motion.li
          onTouchEnd={() => setExpanded(null)}
          onClick={() => setExpanded(null)}
          className={"expanded-item-header"}
          layoutId={layoutId}
          transition={{type: 'spring', bounce: 0.2, duration: 3}}
          >
          <h2 className="h2-left">> {title}</h2>
          <h2 className="h2-right">{subtitle}</h2>
        </motion.li>

        <motion.div
          initial={{opacity: 0, y: 150, backgroundColor: 'rgba(255,255,255,0)'}}
          animate={{opacity: 1, y: 0, backgroundColor: 'rgba(255,255,255,0.8)'}}
          transition={{type: 'spring', bounce: 0.3, duration: 3, delay: 0.4}}
          exit={{opacity: 0, y: 150, transition:{duration: 0.8}}}
          className={"expanded-item-content"}
          >
          <div className={"underline-link"}>
            <a href={href} target="_blank" rel="noopener noreferrer" >
              <h2 className={"h2-left"}>Visit website</h2>
              <div className={"word-underline"}></div>
              <div className={"arrow-container"}>
                <div><div className={"word-underline-arrow"}></div></div>
              </div>
            </a>
          </div>
          <br/>
          <div className={"tech-used"}>
            <h3>Technologies used</h3>

            <div>
              <div>
                <h4>Frontend</h4>
                <ul>
                  {projectDetails[id].technologies_used.frontend.map( tech => {
                    return <li>{tech}</li>
                  })}
                </ul>
              </div>
              <div>
                <h4>Backend</h4>
                <ul>
                  {projectDetails[id].technologies_used.backend.map( tech => {
                    return <li>{tech}</li>
                  })}
                </ul>
              </div>
            </div>
          </div>

        </motion.div>
      </div>
      <div className={"image-carousel"}>
        {projectDetails[id].images.map( content => {
          let image;
          if (content.type === 'image') {
            image = <img src={content.url} alt={content.alt}/>
          } else {
            image = <video src={content.url} autoPlay={true} loop={true} webkit-playsinline="true" playsInline={true} style={{width: '100%', height: '100%'}}/>
          }
          return image
        })}
      </div>
    </motion.div>
  )
}

export default ExpandedItem;
