import React, {useState} from 'react';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';

import ExpandedItem from '../components/expanded_item';
import Item from '../components/item';


function AnimatedItemGroup({setHovered, hovered, setExpanded, expanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  // console.log(expanded)
  return(
    <AnimateSharedLayout type="crossfade">

      <Item
        layoutId={`portfolio-item-${index}`}
        id={id}
        index={index}
        href={href}
        title={title}
        hovered={hovered}
        subtitle={subtitle}
        setExpanded={setExpanded}
        setHovered={setHovered}/>

      {expanded === index &&
        <div
          className={"close-expanded"}
          onTouchEnd={() => setExpanded(null)}
          onClick={() => setExpanded(null)}
          >

        </div>
      }

      <AnimatePresence>
        {expanded === index &&
          <ExpandedItem
            layoutId={`portfolio-item-${index}`}
            id={id}
            index={index}
            href={href}
            title={title}
            subtitle={subtitle}
            expanded={expanded}
            setExpanded={setExpanded}/>
        }
      </AnimatePresence>
    </AnimateSharedLayout>
  )
}

export default AnimatedItemGroup;
