import React, {useState} from 'react';
import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';


function Item({id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  const [hovered, setHovered] = useState(null)
  const [clicked, setClicked] = useState(null)


  return(
      <AnimateSharedLayout>
        <motion.li id={id} onHoverStart={() => setHovered(index)} onHoverEnd={() => setHovered(null)} onTouchStart={() => setHovered(index)}>
          <a href={href} target="_blank" rel="noopener noreferrer">
            <h2 className="h2-left">> {title}</h2>
            <div className={"word-underline"}></div>
            <div className={"arrow-container"}>
              <div><div className={"word-underline-arrow"}></div></div>
            </div>
          </a>
          <h2 className="h2-right">{subtitle}</h2>
        </motion.li>
      </AnimateSharedLayout>
    )
}

export default Item;
