import React, {useState} from 'react';
import { motion, AnimateSharedLayout } from 'framer-motion';


function ExpandedItem({layoutId, setExpanded, id, index, href, title, subtitle}) {
  // const [open, setOpen] = useState(false);
  // const [hovered, setHovered] = useState(null)

  return(

    <div className={"expanded-item"}>
      <div>
        <motion.li
          onTouchEnd={() => setExpanded(null)}
          onClick={() => setExpanded(null)}
          className={"expanded-item-header"}
          transition={{duration: 1}}
          layoutId={layoutId}
          >
          <motion.h2 layoutId={`left-${layoutId}`} className="h2-left">> {title}</motion.h2>
          <motion.h2 layoutId={`right-${layoutId}`} className="h2-right">{subtitle}</motion.h2>
        </motion.li>

        <motion.div
          initial={{opacity: 0, y: 100}}
          animate={{opacity: 1, y: 0}}
          transition={{type: 'spring', bounce: 0.5, duration: 2}}
          exit={{opacity: 0, transition:{duration: 0.8}}}
          className={"expanded-item-content"}
          >
          <a href={href} target="_blank" rel="noopener noreferrer">
            <h2 className="h2-left">Visit website</h2>
            <div className={"word-underline"}></div>
            <div className={"arrow-container"}>
              <div><div className={"word-underline-arrow"}></div></div>
            </div>
          </a>
        </motion.div>
      </div>

    </div>
  )
}

export default ExpandedItem;
