import React, {useState} from 'react';
import './App.scss';

import { motion, AnimatePresence, AnimateSharedLayout } from 'framer-motion';


import Content from './containers/content'

function App() {

  const [expanded, setExpanded] = useState(false)

  return (
    <div className="App">
      {/*<Content/>*/}

     <AnimateSharedLayout type="crossfade">

        <motion.div
          onClick={() => setExpanded(!expanded)}
          layoutId={"div"}
          style={{opacity: 1,height: '100px', width: '100px', backgroundColor: 'red', position: 'fixed', top: 0, left: 0}}>

        </motion.div>

        <AnimatePresence initial={false}>
          {expanded &&
            <motion.div
              transition={{type: 'spring', bounce: 0.2, duration: 0.2}}
              // transition={layoutTransition}
              onClick={() => setExpanded(!expanded)}
              layoutId={"div"}
              style={{height: '100px', width: '100px', backgroundColor: 'blue', position: 'fixed', bottom: 0, right: 0}}>

            </motion.div>
          }
        </AnimatePresence>
      </AnimateSharedLayout>

    </div>
  );
}

export default App;
